CREATE TABLE alimento (
                          id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                          nome VARCHAR(255) NOT NULL,
                          calorias VARCHAR(55) NOT NULL,
                          proteinas VARCHAR(55) NOT NULL,
                          carboidratos VARCHAR(55) NOT NULL,
                          lipidios VARCHAR(55) NOT NULL,
                          comunidade BOOLEAN NOT NULL DEFAULT FALSE
);
ALTER TABLE alimento ADD CONSTRAINT pk_descricao PRIMARY KEY (id);

CREATE TABLE usuario (
                         id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                         nome VARCHAR(255) NOT NULL,
                         email VARCHAR(255) NOT NULL,
                         senha VARCHAR(100) NOT NULL,
                         data_nascimento DATE NOT NULL,
                         altura INTEGER,
                         peso VARCHAR(5),
                         sexo VARCHAR(1),
                         imagem_perfil VARCHAR(511),
                         pontuacao VARCHAR(511) NOT NULL DEFAULT 0,
                         titulo VARCHAR(55),
                         imc VARCHAR(10),
                         gasto_calorico INTEGER,
                         agua_recomendacao INTEGER,
                         calorias_recomendacao INTEGER,
                         proteinas_recomendacao INTEGER,
                         carboidratos_recomendacao INTEGER,
                         lipidios_recomendacao INTEGER,
                         meta VARCHAR(1) DEFAULT 'M',
                         token_senha VARCHAR(30),
                         atividade_fisica VARCHAR(1) DEFAULT 'P',
                         categoria_titulo VARCHAR(10),
                         ativo BOOLEAN NOT NULL DEFAULT true
);

ALTER TABLE usuario ADD CONSTRAINT pk_usuario PRIMARY KEY (id);
ALTER TABLE usuario ADD CONSTRAINT uk_usuario_email UNIQUE (email);
ALTER TABLE usuario ADD CONSTRAINT ck_usuario_sexo CHECK (sexo IN ('M', 'F'));
ALTER TABLE usuario ADD CONSTRAINT ck_usuario_meta CHECK (sexo IN ('P', 'G', 'M'));
ALTER TABLE usuario ADD CONSTRAINT ck_usuario_atividade_fisica CHECK (sexo IN ('P', 'M', 'I'));
ALTER TABLE usuario ADD CONSTRAINT ck_usuario_categoria_titulo CHECK (categoria_titulo IN ('BRONZE','PRATA','OURO', 'PLATINA'));


CREATE TABLE conversa (
                          id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                          primeiro_usuario_id BIGINT NOT NULL,
                          segundo_usuario_id BIGINT NOT NULL
);

ALTER TABLE conversa ADD CONSTRAINT pk_conversa PRIMARY KEY (id);
ALTER TABLE conversa ADD CONSTRAINT fk_conversa_usuario_1 FOREIGN KEY (primeiro_usuario_id) REFERENCES usuario;
ALTER TABLE conversa ADD CONSTRAINT fk_conversa_usuario_2 FOREIGN KEY (segundo_usuario_id) REFERENCES usuario;

CREATE TABLE mensagem (
                          id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                          conversa_id BIGINT NOT NULL,
                          remetente_id BIGINT NOT NULL,
                          texto VARCHAR(500) NOT NULL
);

ALTER TABLE mensagem ADD CONSTRAINT pk_mensagem PRIMARY KEY (id);
ALTER TABLE mensagem ADD CONSTRAINT fk_mensagem_conversa FOREIGN KEY (conversa_id) REFERENCES conversa;
ALTER TABLE mensagem ADD CONSTRAINT fk_mensagem_usuario FOREIGN KEY (remetente_id) REFERENCES usuario;


CREATE TABLE amizade (
                         id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                         seguidor BIGINT NOT NULL,
                         seguido BIGINT NOT NULL
);
ALTER TABLE amizade ADD CONSTRAINT pk_amizade PRIMARY KEY (id);
ALTER TABLE amizade ADD CONSTRAINT uk_amizade UNIQUE (seguidor,seguido);
ALTER TABLE amizade ADD CONSTRAINT fk_amizade_usuario_1 FOREIGN KEY (seguidor) REFERENCES usuario;
ALTER TABLE amizade ADD CONSTRAINT fk_amizade_usuario_2 FOREIGN KEY (seguido) REFERENCES usuario;

CREATE TABLE peso (
                      id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                      valor VARCHAR(5) NOT NULL,
                      usuario_id BIGINT NOT NULL,
                      data_registro DATE NOT NULL
);
ALTER TABLE peso ADD CONSTRAINT pk_peso PRIMARY KEY (id);
ALTER TABLE peso ADD CONSTRAINT fk_peso_usuario_id FOREIGN KEY (usuario_id) references usuario;
ALTER TABLE peso ADD CONSTRAINT uk_peso UNIQUE (usuario_id, valor, data_registro);

CREATE TABLE agua (
                      id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                      quantidade INTEGER NOT NULL DEFAULT 0,
                      usuario_id BIGINT NOT NULL,
                      data_registro DATE NOT NULL
);
ALTER TABLE agua ADD CONSTRAINT pk_agua PRIMARY KEY (id);
ALTER TABLE agua ADD CONSTRAINT fk_agua_usuario_id FOREIGN KEY (usuario_id) references usuario;
ALTER TABLE agua ADD CONSTRAINT uk_agua UNIQUE (usuario_id, data_registro);


CREATE TABLE refeicao (
                          id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                          usuario_id BIGINT NOT NULL,
                          dia DATE NOT NULL,
                          periodo VARCHAR(2) NOT NULL,
                          calorias INTEGER,
                          proteinas INTEGER,
                          carboidratos INTEGER,
                          lipidios INTEGER
);
ALTER TABLE refeicao ADD CONSTRAINT pk_refeicao PRIMARY KEY (id);
ALTER TABLE refeicao ADD CONSTRAINT fk_refeicao_usuario_id FOREIGN KEY (usuario_id) references usuario;
ALTER TABLE refeicao ADD CONSTRAINT uk_refeicao UNIQUE (usuario_id, dia, periodo);
ALTER TABLE refeicao ADD CONSTRAINT ck_refeicao_periodo CHECK (periodo IN ('CM', 'LM','AL','LT','JA','CE'));

CREATE TABLE refeicao_alimento(
                                  id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                                  refeicao_id BIGINT NOT NULL,
                                  alimento_id BIGINT NOT NULL,
                                  quantidade VARCHAR(10) NOT NULL
);
ALTER TABLE refeicao_alimento ADD CONSTRAINT pk_refeicao_alimento PRIMARY KEY (id);
ALTER TABLE refeicao_alimento ADD CONSTRAINT fk_refeicao_alimento_refeicao_id FOREIGN KEY (refeicao_id) REFERENCES refeicao;
ALTER TABLE refeicao_alimento ADD CONSTRAINT fk_refeicao_alimento_alimento_id FOREIGN KEY (alimento_id) REFERENCES alimento;
ALTER TABLE refeicao_alimento ADD CONSTRAINT uk_refeicao_alimento UNIQUE (refeicao_id, alimento_id);

CREATE TABLE receita (
                         id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                         usuario_id BIGINT NOT NULL,
                         criador_id BIGINT NOT NULL,
                         titulo VARCHAR(50) NOT NULL,
                         descricao VARCHAR(255),
                         data_criacao DATE NOT NULL,
                         privado BOOLEAN NOT NULL DEFAULT FALSE,
                         copia BOOLEAN NOT NULL DEFAULT FALSE,
                         calorias INTEGER,
                         proteinas INTEGER,
                         carboidratos INTEGER,
                         lipidios INTEGER,
                         nota VARCHAR(3),
                         modo_preparo VARCHAR(511) NOT NULL,
                         imagem VARCHAR(511)
);
ALTER TABLE receita ADD CONSTRAINT pk_receita PRIMARY KEY (id);
ALTER TABLE receita ADD CONSTRAINT fk_receita_usuario_id FOREIGN KEY (usuario_id) references usuario;

CREATE TABLE avaliacao_receita (
                                   id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                                   receita_id BIGINT NOT NULL,
                                   usuario_id BIGINT NOT NULL,
                                   nota INTEGER NOT NULL CHECK ( nota BETWEEN 1 and 5)
);
ALTER TABLE avaliacao_receita ADD CONSTRAINT pk_avaliacao_receita PRIMARY KEY (id);
ALTER TABLE avaliacao_receita ADD CONSTRAINT fk_avaliacao_receita_usuario_id FOREIGN KEY (usuario_id) REFERENCES usuario;
ALTER TABLE avaliacao_receita ADD CONSTRAINT fk_avaliacao_receita_receita_id FOREIGN KEY (receita_id) REFERENCES receita;
ALTER TABLE avaliacao_receita ADD CONSTRAINT uk_avaliacao_receita UNIQUE (receita_id, usuario_id);


CREATE TABLE receita_alimento(
                                 id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                                 receita_id BIGINT NOT NULL,
                                 alimento_id BIGINT NOT NULL,
                                 quantidade VARCHAR(10) NOT NULL DEFAULT 100

);
ALTER TABLE receita_alimento ADD CONSTRAINT pk_receita_alimento PRIMARY KEY (id);
ALTER TABLE receita_alimento ADD CONSTRAINT fk_receita_alimento_receita_id FOREIGN KEY (receita_id) REFERENCES receita;
ALTER TABLE receita_alimento ADD CONSTRAINT fk_receita_alimento_alimento_id FOREIGN KEY (alimento_id) REFERENCES alimento;
ALTER TABLE receita_alimento ADD CONSTRAINT uk_receita_alimento UNIQUE (receita_id, alimento_id);

CREATE TABLE post (
                      id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                      usuario_id BIGINT NOT NULL,
                      texto VARCHAR(511),
                      data_inclusao TIMESTAMP NOT NULL,
                      imagem VARCHAR(511),
                      privado BOOLEAN NOT NULL DEFAULT FALSE
);
ALTER TABLE post ADD CONSTRAINT pk_post PRIMARY KEY (id);
ALTER TABLE post ADD CONSTRAINT fk_post_usuario_id FOREIGN KEY (usuario_id) REFERENCES usuario;

CREATE TABLE comentario_post (
                                 id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                                 post_id BIGINT NOT NULL,
                                 usuario_id BIGINT NOT NULL,
                                 texto VARCHAR(255) NOT NULL
);
ALTER TABLE comentario_post ADD CONSTRAINT pk_comentario_post PRIMARY KEY (id);
ALTER TABLE comentario_post ADD CONSTRAINT fk_comentario_post_usuario_id FOREIGN KEY (usuario_id) REFERENCES usuario;
ALTER TABLE comentario_post ADD CONSTRAINT fk_comentario_post_post_id FOREIGN KEY (post_id) REFERENCES post;


CREATE TABLE curtida_post (
                              id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                              post_id BIGINT NOT NULL,
                              usuario_id BIGINT NOT NULL
);
ALTER TABLE curtida_post ADD CONSTRAINT pk_curtida_post PRIMARY KEY (id);
ALTER TABLE curtida_post ADD CONSTRAINT uk_curtida_post UNIQUE (post_id, usuario_id);
ALTER TABLE curtida_post ADD CONSTRAINT fk_curtida_post_usuario_id FOREIGN KEY (usuario_id) REFERENCES usuario;
ALTER TABLE curtida_post ADD CONSTRAINT fk_curtida_post_post_id FOREIGN KEY (post_id) REFERENCES post;

CREATE TABLE conquista (
                           id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                           valor BIGINT NOT NULL,
                           descricao VARCHAR(100) NOT NULL,
                           categoria VARCHAR(10) NOT NULL,
                           recompensa VARCHAR(100) NOT NULL,
                           tipo VARCHAR(20) NOT NULL
);
ALTER TABLE conquista ADD CONSTRAINT pk_conquista PRIMARY KEY (id);
ALTER TABLE conquista ADD CONSTRAINT ck_conquista_categoria CHECK (categoria IN ('BRONZE','PRATA','OURO', 'PLATINA'));

CREATE TABLE conquista_usuario (
                                   id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
                                   usuario_id BIGINT NOT NULL,
                                   conquista_id BIGINT NOT NULL,
                                   desbloqueada BOOLEAN NOT NULL DEFAULT FALSE,
                                   progresso BIGINT NOT NULL DEFAULT 0,
                                   data_alteracao DATE
);
ALTER TABLE conquista_usuario ADD CONSTRAINT pk_conquista_usuario PRIMARY KEY (id);
ALTER TABLE conquista_usuario ADD CONSTRAINT fk_conquista_usuario_usuario_id FOREIGN KEY (usuario_id) REFERENCES usuario;
ALTER TABLE conquista_usuario ADD CONSTRAINT fk_conquista_usuario_conquista_id FOREIGN KEY (conquista_id) REFERENCES conquista;


